import React, { useRef } from "react";
import CollapseWrapper from "../common/collapse";
import { checkNode } from '@testing-library/jest-dom/dist/utils';

const UseRefExercise = () => {
  const blockRef = useRef();

  const blockSettings = {
    caption: {
      textContent: 'text'
    },
    size: {
      width: '150px',
      height: '80px'
    }
  }

  const handleChangeBlock = () => {
    console.log(blockRef.current);

    blockRef.current.lastChild.innerText = 'text';
    // blockRef.current.style.width = '150px';
    // blockRef.current.style.height = '80px';
    const rest = blockRef.current.style;
    blockRef.current.style = {
      ...rest,
      height: 150,
      width: 40
    }
  };

  return (
    <CollapseWrapper title="Упражнение">
      <p className="mt-3">
        У вас есть блок, у которого заданы ширина и высота. Добавьте
        кнопку, при нажатии которой изменятся следующие свойства:
      </p>
      <ul>
        <li>Изменится содержимое блока на &quot;text&quot;</li>
        <li>высота и ширина станут равны 150 и 80 соответственно</li>
      </ul>
      <button
        className='btn btn-secondary mb-2'
        onClick={handleChangeBlock}
      >
        Change block
      </button>
      <div
        ref={blockRef}
        className="bg-primary d-flex flex-row justify-content-center align-items-center rounded"
        style={{
          height: 40,
          width: 60,
          color: "white"
        }}
      >
        <small>Блок</small>
      </div>
    </CollapseWrapper>
  );
};

export default UseRefExercise;
