import React from 'react';

const SimpleComponent = ({ isA}) => {
  // console.log('SimpleComponent', isAuth);
  const isAuth = localStorage.getItem('user');
  const handleLogin = () => {
    localStorage.setItem('user', 'true');
  }
  const handleLogout = () => {
    localStorage.removeItem('user');
  }
  return (
    <>
      {!isAuth ? <button onClick={handleLogin}>Login</button> : <button onClick={handleLogout}>Logout</button>}
    </>
  );
}

export default SimpleComponent;
